const { engines } = require('../package.json');
const version = engines.node;
const semver = require('semver');

if (!semver.satisfies(process.version, version)) {
  console.error(
    `Required node version ${version} not satisfied with current version ${process.version}.`,
  );
  process.exit(1);
}

// Declaration for the 'react-visual-grid' module
declare module 'react-visual-grid' {
  // Import necessary types from React
  import { FC, ReactNode } from 'react';

  // Define the props interface for the VisualGrid component
  interface VisualGridProps {
    // Array of items to be displayed in the grid
    items: any[];
    // Component to render each item
    itemComponent: FC<any>;
    // Optional additional props for each item
    itemProps?: any;
    // Optional gap between grid items
    gridGap?: number;
    // Optional minimum width for grid columns
    minColumnWidth?: number;
    // Optional maximum width for grid columns
    maxColumnWidth?: number;
  }

  // Export the VisualGrid component as a Functional Component with VisualGridProps
  export const VisualGrid: FC<VisualGridProps>;
}

import express from 'express';
import fs from 'fs';
import path from 'path';

const router = express.Router();

router.get('/getCurrentDirectory', (req, res) => {
  try {
    const mainDir = process.env.MAIN_DIRECTORY;

    if (!mainDir) {
      throw new Error('MAIN_DIRECTORY is not set in environment variables');
    }

    if (fs.existsSync(mainDir)) {
      res.status(200).json({ currentDirectory: mainDir });
    } else {
      res.status(404).json({ error: 'Main directory not found' });
    }
  } catch (error) {
    console.error('Error in getCurrentDirectory:', error);
    res.status(500).json({ error: 'Internal Server Error' });
  }
});

export default router;

import express from 'express';
import fs from 'fs';
import path from 'path';
import { ImageInfo } from '../types.js';
import { isImageFile, getImageDimensions } from '../utils/imageUtils.js';

const router = express.Router();

router.get('/images', async (req, res) => {
  const { folder } = req.query;

  if (!folder) {
    return res.status(400).json({ error: 'Folder parameter is required' });
  }

  if (typeof folder !== 'string') {
    return res.status(400).json({ error: 'Folder parameter must be a string' });
  }

  if (folder.includes('..') || folder.includes('\\')) {
    return res.status(400).json({ error: 'Invalid folder path' });
  }

  try {
    const images = await getImages(folder);
    res.status(200).json(images);
  } catch (error: any) {
    console.error('Error in getImages:', error);
    if (error.message.includes('Folder not found')) {
      res.status(404).json({ error: `Folder not found: ${folder}` });
    } else {
      res.status(500).json({ error: 'Failed to fetch images', details: error.message });
    }
  }
});

async function getImages(folder: string): Promise<ImageInfo[]> {
  const mainDirectory = process.env.MAIN_DIRECTORY || process.cwd();
  const imagesDirectory = path.join(mainDirectory, folder);

  const normalizedImagesDir = path.normalize(imagesDirectory);
  const normalizedMainDir = path.normalize(mainDirectory);
  if (!normalizedImagesDir.startsWith(normalizedMainDir)) {
    throw new Error('Invalid folder path: Access denied');
  }

  if (!fs.existsSync(imagesDirectory)) {
    throw new Error(`Folder not found: ${folder}`);
  }

  const fileNames = fs.readdirSync(imagesDirectory);

  // First collect the promises with proper typing
  const imagePromises = fileNames
    .filter(isImageFile)
    .map(async (fileName) => {
      try {
        const id = fileName.replace(/\.[^/.]+$/, '');
        const filePath = path.join(imagesDirectory, fileName);
        const { width, height } = await getImageDimensions(filePath);

        return {
          id,
          src: `/api/image?folder=${encodeURIComponent(folder)}&file=${encodeURIComponent(fileName)}`,
          alt: fileName,
          title: fileName,
          width,
          height,
        } satisfies ImageInfo;
      } catch (error) {
        console.error(`Error processing image ${fileName}:`, error);
        return null;
      }
    });

  // Then resolve promises and filter out nulls with type guard
  const results = await Promise.all(imagePromises);
  const images: ImageInfo[] = results.filter((image): image is ImageInfo => image !== null);

  return images;
}

export default router;

import { motion, useTransform, useViewportScroll } from 'framer-motion';
import React, {
  useCallback,
  useEffect,
  useMemo,
  useRef,
  useState,
} from 'react';
import Lightbox from 'yet-another-react-lightbox';
import Captions from 'yet-another-react-lightbox/plugins/captions';
import 'yet-another-react-lightbox/plugins/captions.css';
import Counter from 'yet-another-react-lightbox/plugins/counter';
import 'yet-another-react-lightbox/plugins/counter.css';
import Thumbnails from 'yet-another-react-lightbox/plugins/thumbnails';
import 'yet-another-react-lightbox/plugins/thumbnails.css';
import Zoom from 'yet-another-react-lightbox/plugins/zoom';
import 'yet-another-react-lightbox/styles.css';
import styles from '../styles/ImageFeed.module.scss';
import { ImageInfo, ViewMode } from '../types.js';
import { truncateImageTitle } from '../utils/stringUtils.js';
import ImageRow from './ImageRow.js';
import ImageSkeleton from './ImageSkeleton.js';
// import Lottie from 'react-lottie';
import { CSSProperties } from 'react';
import useWindowSize from '../hooks/useWindowSize.js';
import AnimationSystem from '../utils/AnimationSystem';
import {
  calculateGapSize,
  calculateLayout,
  distributeImages,
  LayoutConfig,
  MIN_IMAGE_WIDTH,
} from '../utils/layoutCalculator';
import WorkerPool from '../workers/workerPool';
import RippleEffect from './RippleEffect';
import { BannerView, CarouselView, MasonryView } from './views';

// Define the props interface for ImageFeed component
interface ImageFeedProps {
  images: ImageInfo[];
  isLoading: boolean;
  isGrouped: boolean;
  zoom: number;
  viewMode: ViewMode;
}

interface CustomStyle extends CSSProperties {
  '--energy-color'?: string;
  '--ripple-x'?: string;
  '--ripple-y'?: string;
  '--ripple-strength'?: string;
}

// Define the ImageFeed component
const ImageFeed: React.FC<ImageFeedProps> = ({
  images,
  isLoading,
  isGrouped,
  zoom,
  viewMode,
}) => {
  const windowSize = useWindowSize();
  const feedRef = useRef<HTMLDivElement>(null);
  const [containerWidth, setContainerWidth] = useState(0);
  const [columns, setColumns] = useState(4);
  const [lightboxIndex, setLightboxIndex] = useState<number>(-1);
  const [lightboxImages, setLightboxImages] = useState<ImageInfo[]>([]);
  const [rowTransforms, setRowTransforms] = useState<number[]>([]);
  const { scrollY } = useViewportScroll();
  const y1 = useTransform(scrollY, [0, 300], [0, 200]);
  const y2 = useTransform(scrollY, [0, 300], [0, -200]);
  const [processedImages, setProcessedImages] = useState<{
    [key: string]: { low: string; high: string };
  }>({});
  const animationSystem = useMemo(() => AnimationSystem.getInstance(), []);
  const [energyColor, setEnergyColor] = useState('rgba(255, 255, 255, 0.5)');
  const [ripples, setRipples] = useState<
    Array<{
      id: number;
      x: number;
      y: number;
      strength: number;
    }>
  >([]);
  const [waterEffectReady, setWaterEffectReady] = useState(false);
  const waterEffectRef = useRef<HTMLDivElement>(null);

  // Use global worker pool instead of creating new workers
  const imageProcessor = useMemo(
    () => WorkerPool.getInstance().getImageProcessor(),
    [],
  );

  // Cleanup worker when component unmounts
  useEffect(() => {
    return () => {
      if (viewMode === ViewMode.GRID) {
        WorkerPool.getInstance().cleanup();
      }
    };
  }, [viewMode]);

  // Reset states when folder or view mode changes
  useEffect(() => {
    setLightboxIndex(-1);
    setLightboxImages([]);
    setProcessedImages({});
    setRowTransforms([]);
  }, [images, viewMode]);

  // Enhanced container width management
  useEffect(() => {
    const updateContainerWidth = () => {
      if (!feedRef.current) return;

      const rect = feedRef.current.getBoundingClientRect();
      // Use the actual measured width from the container
      const newWidth = Math.max(MIN_IMAGE_WIDTH, rect.width);

      if (newWidth !== containerWidth) {
        setContainerWidth(newWidth);
        const config: LayoutConfig = {
          containerWidth: newWidth,
          zoom,
          viewMode,
          isGrouped,
        };
        const layout = calculateLayout(config);
        setColumns(layout.columns);
      }
    };

    updateContainerWidth();

    // Add resize observer for more accurate width updates
    const resizeObserver = new ResizeObserver(updateContainerWidth);
    if (feedRef.current) {
      resizeObserver.observe(feedRef.current);
    }

    window.addEventListener('resize', updateContainerWidth);

    return () => {
      resizeObserver.disconnect();
      window.removeEventListener('resize', updateContainerWidth);
    };
  }, [zoom, viewMode, isGrouped, containerWidth]);

  // Update energy color based on system state
  useEffect(() => {
    const updateColor = () => {
      animationSystem.updateColorEnergy();
      const hue = getComputedStyle(document.documentElement)
        .getPropertyValue('--energy-hue')
        .trim();
      setEnergyColor(`hsla(${hue}, 80%, 60%, 0.5)`);
    };

    const interval = setInterval(updateColor, 50);
    return () => clearInterval(interval);
  }, [animationSystem]);

  // Modified groupedImages calculation with better error handling
  const groupedImages = useMemo(() => {
    if (!Array.isArray(images) || images.length === 0) {
      console.warn('No images provided or invalid images array');
      return [];
    }

    const validImages = images.filter(
      (image) =>
        image &&
        typeof image === 'object' &&
        'width' in image &&
        'height' in image &&
        image.width > 0 &&
        image.height > 0,
    );

    if (validImages.length !== images.length) {
      console.warn(
        `Filtered out ${images.length - validImages.length} invalid images`,
      );
    }

    if (!isGrouped) {
      return validImages.map((image) => ({
        key: image.id,
        images: [image],
        isCarousel: false,
      }));
    }

    const groups: { [key: string]: ImageInfo[] } = {};
    validImages.forEach((image) => {
      const processedTitle = truncateImageTitle(image.alt);
      if (!groups[processedTitle]) {
        groups[processedTitle] = [];
      }
      groups[processedTitle].push(image);
    });

    return Object.entries(groups).map(([key, group]) => ({
      key,
      images: group,
      isCarousel: group.length > 1,
    }));
  }, [images, isGrouped]);

  // Enhanced groupedRows calculation with better error handling and viewMode awareness
  const groupedRows = useMemo(() => {
    if (
      !containerWidth ||
      groupedImages.length === 0 ||
      viewMode !== ViewMode.GRID
    )
      return [];

    const firstImages = groupedImages
      .map((group) => group.images[0])
      .filter((image) => image && image.width > 0 && image.height > 0);

    if (firstImages.length === 0) return [];

    // Uses our optimized distributeImages function with sorted cache
    return distributeImages(
      firstImages,
      containerWidth,
      zoom,
      200, /* base row height */
    );
  }, [groupedImages, zoom, containerWidth, viewMode]);

  // Handle image overflow
  const handleImageOverflow = useCallback((image: ImageInfo) => {
    console.warn('Image overflow detected:', image.id);
    // Implement any necessary overflow handling logic here
  }, []);

  // Update handleImageClick to work with grouped images and set lightbox plugins
  const handleImageClick = useCallback(
    (clickedImage: ImageInfo) => {
      const groupIndex = groupedImages.findIndex((group) =>
        group.images.some((img) => img.id === clickedImage.id),
      );
      if (groupIndex !== -1) {
        const group = groupedImages[groupIndex];
        setLightboxIndex(groupIndex);
        setLightboxImages(group.images);
      }
    },
    [groupedImages],
  );

  // Callback function to get the correct image URL
  const getImageUrl = useCallback((imagePath: string) => {
    const cleanPath = imagePath
      .replace(/^(\/|api\/image\/)+/, '')
      .replace(/\\/g, '/');
    return `/api/image/${cleanPath}`;
  }, []);

  // Simplified ripple handler
  const handleRipple = useCallback((x: number, y: number, strength: number) => {
    const rippleId = Date.now();
    setRipples((prev) => {
      const newRipples = prev.slice(-3); // Keep only last 3 ripples for performance
      return [...newRipples, { id: rippleId, x, y, strength }];
    });

    setTimeout(() => {
      setRipples((prev) => prev.filter((r) => r.id !== rippleId));
    }, 1000);
  }, []);

  // Enhanced renderContent
  const renderContent = () => {
    if (!containerWidth) return null;
    const gap = calculateGapSize(zoom);

    const commonProps = {
      onRipple: handleRipple,
      energyColor,
    };

    switch (viewMode) {
    case ViewMode.BANNER:
      return <BannerView images={images} zoom={zoom} {...commonProps} />;
    case ViewMode.MASONRY:
      return <MasonryView images={images} zoom={zoom} {...commonProps} />;
    case ViewMode.CAROUSEL:
      return <CarouselView images={images} zoom={zoom} {...commonProps} />;
    case ViewMode.GRID:
      if (!groupedRows.length) return null;
      return (
        <motion.div
          className={styles.gridContainer}
          style={
              {
                display: 'flex',
                flexDirection: 'column',
                gap: `${gap}px`,
                '--energy-color': energyColor,
              } as CustomStyle
          }
        >
          {groupedRows.map((row, index) => (
            <ImageRow
              key={`row-${index}-${row.images
                .map((img) => img.id)
                .join('-')}`}
              images={row.images}
              onImageClick={handleImageClick}
              columns={columns}
              zoom={zoom}
              isLastRow={index === groupedRows.length - 1}
              rowHeight={row.height}
              groupedImages={groupedImages}
              processedImages={processedImages}
              imageProcessor={imageProcessor}
              gap={gap}
              containerWidth={containerWidth}
              onImageOverflow={handleImageOverflow}
            />
          ))}
        </motion.div>
      );
    default:
      return null;
    }
  };

  return (
    <div
      className="image-feed overflow-x-hidden"
      ref={feedRef}
      style={
        {
          position: 'relative',
          perspective: '1000px',
          gap: `${calculateGapSize(zoom)}px`,
          display: 'flex',
          flexDirection: 'column',
          width: '100%',
          maxWidth: windowSize.width ? `${windowSize.width}px` : '100%',
          overflow: 'hidden',
        } as CustomStyle
      }
    >
      {/* Main content */}
      <div className={styles.feedContent}>{renderContent()}</div>

      {/* Ripple effect overlay */}
      <RippleEffect
        width={windowSize.width || window.innerWidth}
        height={windowSize.height || window.innerHeight}
        resolution={128}
        damping={0.97}
        propagationSpeed={5}
        onReady={() => setWaterEffectReady(true)}
      />

      {isLoading && (
        <ImageSkeleton containerWidth={800} containerHeight={600} />
      )}

      <Lightbox
        slides={lightboxImages.map((image) => ({
          src: image.src,
          alt: image.alt,
          title: truncateImageTitle(image.alt),
          description: `Image ${image.id}`,
        }))}
        open={lightboxIndex >= 0}
        index={lightboxIndex - lightboxIndex}
        close={() => setLightboxIndex(-1)}
        plugins={[
          Captions,
          Counter,
          Zoom,
          ...(lightboxImages.length > 1 ? [Thumbnails] : []),
        ]}
        thumbnails={{
          position: 'bottom',
          width: 120,
          height: 80,
          border: 1,
          borderRadius: 4,
          padding: 4,
          gap: 16,
        }}
        animation={{
          fade: 300,
          swipe: 300,
        }}
        carousel={{
          finite: true,
          preload: 2,
          padding: 0,
          spacing: 30,
          imageFit: 'contain',
        }}
        render={{
          buttonPrev: () => null,
          buttonNext: () => null,
        }}
      />

      <motion.div
        className={styles.parallaxLayer}
        style={{
          y: y1,
          filter: 'hue-rotate(calc(var(--energy-hue, 0) * 1deg))',
        }}
      >
        {/* Add some background elements for parallax effect */}
      </motion.div>
    </div>
  );
};

export default ImageFeed;

import {
  AnimatePresence,
  motion,
  MotionStyle,
  useAnimation,
} from 'framer-motion';
import gsap from 'gsap';
import throttle from 'lodash/throttle';
import React, {
  memo,
  useCallback,
  useEffect,
  useMemo,
  useRef,
  useState,
} from 'react';
import { useIntersectionObserver } from '../hooks/useIntersectionObserver.js';
import styles from '../styles/ImageItem.module.scss';
import { ImageInfo } from '../types.js';
import AnimationSystem from '../utils/AnimationSystem';
import { maintainAspectRatio } from '../utils/layoutCalculator';
import { truncateImageTitle } from '../utils/stringUtils.js';

interface CustomMotionStyle extends MotionStyle {
  '--mouse-x'?: string;
  '--mouse-y'?: string;
}

interface ImageItemProps {
  image: ImageInfo;
  onClick: (image: ImageInfo) => void;
  containerWidth: number;
  containerHeight: number;
  zoom: number;
  groupCount?: number;
  imageProcessor: any;
  onResize?: (width: number, height: number) => void;
  width: number;
  height: number;
  isCarousel: boolean;
  groupImages: ImageInfo[];
  processedImage?: {
    low: string;
    high: string;
  };
  onRipple?: (x: number, y: number, strength: number) => void;
}

const ImageItem: React.FC<ImageItemProps> = ({
  image,
  onClick,
  containerWidth,
  containerHeight,
  zoom,
  groupCount,
  imageProcessor,
  onResize,
  width,
  height,
  isCarousel,
  groupImages,
  processedImage,
  onRipple,
}) => {
  const controls = useAnimation();
  const imageRef = useRef<HTMLImageElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const [isHovered, setIsHovered] = useState(false);
  const [isLoaded, setIsLoaded] = useState(false);
  const [currentImage, setCurrentImage] = useState(
    processedImage?.low || image.src,
  );
  const animationSystem = useMemo(() => AnimationSystem.getInstance(), []);
  const breathingAnimation = useRef<gsap.core.Timeline>();
  const [gestureEnergy, setGestureEnergy] = useState(0);
  const gestureTimeoutRef = useRef<number>();
  const lastMousePosition = useRef<{ x: number; y: number } | null>(null);
  const velocityRef = useRef<{ x: number; y: number }>({ x: 0, y: 0 });

  const [ref, isIntersecting] = useIntersectionObserver({
    threshold: 0,
    rootMargin: '100px',
    triggerOnce: true,
  });

  // Memoized dimensions calculation
  const dimensions = useMemo(
    () => maintainAspectRatio(image, containerWidth, containerHeight),
    [image, containerWidth, containerHeight],
  );

  // Notify parent of size changes using cached dimensions
  useEffect(() => {
    if (onResize) {
      onResize(dimensions.width, dimensions.height);
    }
  }, [dimensions.width, dimensions.height, onResize]);

  // Initialize breathing animation
  useEffect(() => {
    if (containerRef.current && isLoaded) {
      breathingAnimation.current = animationSystem.createBreathingAnimation(
        containerRef.current,
        image.id,
      );
    }

    return () => {
      breathingAnimation.current?.kill();
    };
  }, [isLoaded, image.id, animationSystem]);

  // Optimized hover animation
  const handleHoverStart = useCallback(() => {
    setIsHovered(true);
    if (containerRef.current) {
      gsap.to(containerRef.current, {
        scale: 1.02, // Reduced scale for better performance
        duration: 0.2,
        ease: 'power2.out',
        overwrite: true,
      });
    }
  }, []);

  const handleHoverEnd = useCallback(() => {
    setIsHovered(false);
    if (containerRef.current) {
      gsap.to(containerRef.current, {
        scale: 1,
        duration: 0.15,
        ease: 'power1.out',
        overwrite: true,
      });
    }
  }, []);

  // Optimized mouse move handler with throttle
  const handleMouseMove = useMemo(
    () =>
      throttle((e: React.MouseEvent<HTMLDivElement>) => {
        if (!containerRef.current || !isHovered) return;

        const rect = containerRef.current.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;

        requestAnimationFrame(() => {
          if (containerRef.current) {
            containerRef.current.style.setProperty(
              '--mouse-x',
              `${(x / rect.width) * 100}%`,
            );
            containerRef.current.style.setProperty(
              '--mouse-y',
              `${(y / rect.height) * 100}%`,
            );
          }
        });
      }, 16), // ~60fps
    [isHovered],
  );

  // Enhanced gesture tracking with optimized performance
  const handleMouseMoveEnhanced = useCallback(
    (e: React.MouseEvent<HTMLDivElement>) => {
      if (!containerRef.current) return;

      const rect = containerRef.current.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;
      const normalizedX = (x / rect.width) * 100;
      const normalizedY = (y / rect.height) * 100;

      // Calculate velocity with smoothing
      if (lastMousePosition.current) {
        const dx = e.clientX - lastMousePosition.current.x;
        const dy = e.clientY - lastMousePosition.current.y;

        // Smooth out velocity changes
        velocityRef.current = {
          x: velocityRef.current.x * 0.8 + dx * 0.2,
          y: velocityRef.current.y * 0.8 + dy * 0.2,
        };

        const speed = Math.hypot(velocityRef.current.x, velocityRef.current.y);

        // Only trigger ripple effect for significant movements
        if (speed > 3 && onRipple) {
          onRipple(e.clientX, e.clientY, Math.min(speed / 15, 1));
        }

        // Smoother energy updates
        const newEnergy = Math.min(100, gestureEnergy + speed / 15);
        setGestureEnergy(newEnergy);

        if (gestureTimeoutRef.current) {
          window.clearTimeout(gestureTimeoutRef.current);
        }

        // Gradual energy decay
        gestureTimeoutRef.current = window.setTimeout(() => {
          setGestureEnergy((prev) => Math.max(0, prev * 0.9));
        }, 50);

        // Quick tilt response
        gsap.to(containerRef.current, {
          rotationX: (normalizedY - 50) * 0.1,
          rotationY: (normalizedX - 50) * 0.1,
          duration: 0.15,
          ease: 'power1.out',
          overwrite: true,
        });
      }

      lastMousePosition.current = { x: e.clientX, y: e.clientY };

      if (containerRef.current) {
        containerRef.current.style.setProperty('--mouse-x', `${normalizedX}%`);
        containerRef.current.style.setProperty('--mouse-y', `${normalizedY}%`);
      }
    },
    [gestureEnergy, onRipple],
  );

  // Clean up gesture tracking
  useEffect(() => {
    return () => {
      if (gestureTimeoutRef.current) {
        window.clearTimeout(gestureTimeoutRef.current);
      }
    };
  }, []);

  // Enhanced zoom animation
  useEffect(() => {
    if (!containerRef.current) return;

    const animation = gsap.to(containerRef.current, {
      width: dimensions.width,
      height: dimensions.height,
      ...animationSystem.getAnimationProperties(image.id),
      onComplete: () => {
        if (containerRef.current) {
          containerRef.current.style.willChange = 'auto';
          animationSystem.createMorphAnimation(containerRef.current, image.id);
        }
      },
    });

    return () => {
      animation.kill();
    };
  }, [dimensions.width, dimensions.height, zoom, image.id, animationSystem]);

  // Process image when in view
  useEffect(() => {
    if (isIntersecting && imageProcessor && !processedImage?.high) {
      imageProcessor.postMessage({
        action: 'processImage',
        imageSrc: image.src,
        width: Math.ceil(dimensions.width),
        height: Math.ceil(dimensions.height),
      });
    }
  }, [
    isIntersecting,
    image.src,
    dimensions.width,
    dimensions.height,
    imageProcessor,
    processedImage,
  ]);

  // Update image source
  useEffect(() => {
    if (processedImage?.high && isLoaded) {
      setCurrentImage(processedImage.high);
    }
  }, [processedImage, isLoaded]);

  return (
    <motion.div
      ref={containerRef}
      className={styles.imageItem}
      style={
        {
          width: dimensions.width,
          height: dimensions.height,
          overflow: 'hidden',
          borderRadius: '8px',
          transformOrigin: 'center center',
          willChange: 'transform',
          position: 'relative',
          backfaceVisibility: 'hidden',
          WebkitBackfaceVisibility: 'hidden',
          perspective: 1000,
          WebkitPerspective: 1000,
          '--mouse-x': '50%',
          '--mouse-y': '50%',
          transition: 'transform 0.15s ease-out',
        } as CustomMotionStyle
      }
      onMouseMove={handleMouseMove}
      onMouseLeave={handleHoverEnd}
      onHoverStart={handleHoverStart}
      onHoverEnd={handleHoverEnd}
      onClick={() => {
        animationSystem.addEnergy();
        onClick(image);
      }}
      layout="position"
      layoutDependency={zoom}
    >
      <motion.div
        className={styles.imageWrapper}
        layout
        style={{
          width: '100%',
          height: '100%',
          position: 'relative',
        }}
      >
        <motion.img
          ref={imageRef}
          src={currentImage}
          alt={image.alt}
          className={styles.image}
          loading="lazy"
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            willChange: 'transform',
            backfaceVisibility: 'hidden',
            WebkitBackfaceVisibility: 'hidden',
          }}
          layoutId={`image-${image.id}`}
          onLoad={() => setIsLoaded(true)}
          initial={{ opacity: 0 }}
          animate={{
            opacity: isLoaded ? 1 : 0,
            scale: isLoaded ? 1 : 0.95,
          }}
          transition={{
            duration: 0.3,
            ease: 'easeOut',
          }}
        />

        <AnimatePresence>
          {groupCount && groupCount > 1 && (
            <motion.div
              className={styles.groupCounter}
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.8 }}
            >
              {groupCount}
            </motion.div>
          )}
        </AnimatePresence>

        {isCarousel && groupImages.length > 1 && (
          <div className={styles.carouselIndicator}>{groupImages.length}</div>
        )}

        <motion.div
          className={styles.imageTitle}
          initial={{ opacity: 0, y: 20 }}
          animate={{
            opacity: isHovered ? 1 : 0,
            y: isHovered ? 0 : 20,
          }}
          transition={{
            duration: 0.2,
            ease: 'easeOut',
          }}
        >
          {truncateImageTitle(image.alt)}
        </motion.div>
      </motion.div>
    </motion.div>
  );
};

export default memo(ImageItem, (prevProps, nextProps) => {
  return (
    prevProps.image.src === nextProps.image.src &&
    prevProps.containerWidth === nextProps.containerWidth &&
    prevProps.containerHeight === nextProps.containerHeight &&
    prevProps.zoom === nextProps.zoom &&
    prevProps.groupCount === nextProps.groupCount &&
    prevProps.width === nextProps.width &&
    prevProps.height === nextProps.height &&
    prevProps.isCarousel === nextProps.isCarousel &&
    prevProps.groupImages.length === nextProps.groupImages.length &&
    prevProps.processedImage === nextProps.processedImage
  );
});

import React, { useRef, useEffect, useState, useMemo } from 'react';
import useWindowSize from '../hooks/useWindowSize.js';
import { ImageInfo } from '../types.js';
import ImageItem from './ImageItem.js';
import styles from '../styles/ImageRow.module.scss';
import { motion, useAnimation } from 'framer-motion';
import { createImageProcessor } from '../workers/imageProcessor';
import gsap from 'gsap';
import { calculateImageDimensions, optimizeRowLayout } from '../utils/layoutCalculator';

// Define the props interface for the ImageRow component
interface ImageRowProps {
  images: ImageInfo[];
  onImageClick: (clickedImage: ImageInfo) => void;
  columns: number;
  zoom: number;
  isLastRow: boolean;
  rowHeight: number;
  groupedImages: { key: string; images: ImageInfo[]; isCarousel: boolean }[];
  processedImages: { [key: string]: { low: string; high: string } };
  imageProcessor: ReturnType<typeof createImageProcessor>;
  onImageOverflow?: (image: ImageInfo) => void;
  gap: number;
  containerWidth: number;
}

// Define the ImageRow component
const ImageRow: React.FC<ImageRowProps> = ({
  images,
  onImageClick,
  columns,
  zoom,
  isLastRow,
  rowHeight,
  groupedImages,
  processedImages,
  imageProcessor,
  onImageOverflow,
  gap,
  containerWidth,
}) => {
  const rowRef = useRef<HTMLDivElement>(null);
  const [imageStates, setImageStates] = useState<Map<string, { width: number; height: number }>>(new Map());
  const controls = useAnimation();
  const [isOverflowing, setIsOverflowing] = useState(false);

  // Reset image states when images or groups change
  useEffect(() => {
    setImageStates(new Map());
    setIsOverflowing(false);
  }, [images, groupedImages]);

  const optimizedLayout = useMemo(() => {
    if (!images.length) return null;

    const validImages = images.every(img => img.width > 0 && img.height > 0);
    if (!validImages) return null;

    // Uses cached layout calculations
    return optimizeRowLayout(
      {
        width: containerWidth,
        height: rowHeight,
        gap,
        images,
      },
      containerWidth,
      zoom,
    );
  }, [containerWidth, rowHeight, gap, images.length, zoom]);

  // Enhanced image width calculation with overflow detection
  const imageWidths = useMemo(() => {
    if (!optimizedLayout) return [];

    const totalGapWidth = gap * (images.length - 1);
    const availableWidth = Math.max(0, containerWidth - totalGapWidth);

    // Calculate aspect ratios once
    const aspectRatios = images.map(image => image.width / image.height);
    const totalAspectRatio = aspectRatios.reduce((sum, ratio) => sum + ratio, 0);

    // First pass: calculate proportional widths
    const widths = aspectRatios.map(ratio => {
      const proportion = ratio / totalAspectRatio;
      return Math.floor(availableWidth * proportion);
    });

    // Check if total width exceeds container width
    const totalWidth = widths.reduce((sum, width) => sum + width, 0) + totalGapWidth;
    const newIsOverflowing = totalWidth > containerWidth;

    if (newIsOverflowing !== isOverflowing) {
      setIsOverflowing(newIsOverflowing);
      if (newIsOverflowing && onImageOverflow) {
        onImageOverflow(images[images.length - 1]);
      }
    }

    // Second pass: adjust widths to fit container exactly
    if (newIsOverflowing || totalWidth < containerWidth) {
      const remainingWidth = containerWidth - totalWidth;
      const adjustmentPerImage = Math.floor(remainingWidth / images.length);

      return widths.map((width, index) => {
        // Add any remaining pixels to the last image
        if (index === widths.length - 1) {
          const usedWidth = widths.slice(0, -1).reduce((sum, w) => sum + w + adjustmentPerImage, 0) + totalGapWidth;
          return Math.max(0, containerWidth - usedWidth);
        }
        return Math.max(0, width + adjustmentPerImage);
      });
    }

    return widths;
  }, [images, containerWidth, gap, optimizedLayout, isOverflowing, onImageOverflow]);

  // Handle image resize with debouncing
  const createHandleImageResize = (imageId: string) => (width: number, height: number) => {
    setImageStates((prev: Map<string, { width: number; height: number }>) => {
      const newMap = new Map(prev);
      newMap.set(imageId, { width, height });
      return newMap;
    });
  };

  if (!images || images.length === 0 || !optimizedLayout) {
    return null;
  }

  return (
    <motion.div
      ref={rowRef}
      className={styles.imageRow}
      animate={controls}
      initial={false}
      style={{
        display: 'flex',
        gap: `${gap}px`,
        height: `${optimizedLayout.height}px`,
        marginBottom: `${gap}px`,
        position: 'relative',
        willChange: 'transform',
        width: '100%',
        maxWidth: `${containerWidth}px`,
        overflow: 'hidden',
        justifyContent: 'flex-start',
        alignItems: 'stretch',
        flexWrap: 'nowrap',
      }}
      layout
      transition={{
        type: 'spring',
        stiffness: 300,
        damping: 30,
      }}
    >
      {images.map((image, index) => {
        const group = groupedImages.find(g => g.images.some(img => img.id === image.id));
        const width = imageWidths[index];

        if (!width) return null;

        return (
          <motion.div
            key={image.id}
            className={styles.imageWrapper}
            style={{ 
              width: `${width}px`,
              height: `${optimizedLayout.height}px`,
              flexShrink: 0,
              flexGrow: 0,
              position: 'relative',
              overflow: 'hidden',
            }}
            layout
          >
            <ImageItem
              image={image}
              onClick={() => onImageClick(image)}
              containerWidth={width}
              containerHeight={optimizedLayout.height}
              width={width}
              height={optimizedLayout.height}
              zoom={zoom}
              isCarousel={group?.isCarousel || false}
              groupImages={group?.images || []}
              processedImage={processedImages[image.id]}
              imageProcessor={imageProcessor}
              onResize={createHandleImageResize(image.id)}
            />
          </motion.div>
        );
      })}
    </motion.div>
  );
};

export default ImageRow;

