import React, { useCallback, useEffect, useRef } from 'react';

interface Point {
  x: number;
  y: number;
  radius: number;
  strength: number;
  timestamp: number;
}

interface RippleEffectProps {
  width: number;
  height: number;
  resolution?: number;
  damping?: number;
  propagationSpeed?: number;
  onReady?: () => void;
}

const RippleEffect: React.FC<RippleEffectProps> = ({
  width,
  height,
  resolution = 256,
  damping = 0.97,
  propagationSpeed = 5,
  onReady,
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const bufferRef = useRef<{ current: number[][]; previous: number[][] }>({
    current: [],
    previous: [],
  });
  const ripplesRef = useRef<Point[]>([]);
  const frameRef = useRef<number>(0);
  const contextRef = useRef<CanvasRenderingContext2D | null>(null);

  // Initialize grid buffers
  const initializeBuffers = useCallback(() => {
    const buffer = {
      current: Array(resolution)
        .fill(0)
        .map(() => Array(resolution).fill(0)),
      previous: Array(resolution)
        .fill(0)
        .map(() => Array(resolution).fill(0)),
    };
    bufferRef.current = buffer;
  }, [resolution]);

  // Add a new ripple to the simulation
  const addRipple = useCallback(
    (x: number, y: number, strength: number = 1) => {
      const ripple: Point = {
        x: x * resolution,
        y: y * resolution,
        radius: 0,
        strength: Math.min(Math.max(strength, 0), 1),
        timestamp: Date.now(),
      };
      ripplesRef.current.push(ripple);

      // Add initial displacement to the buffer
      const buffer = bufferRef.current.current;
      const radius = 2;
      const intensity = ripple.strength * 5;

      for (let i = -radius; i <= radius; i++) {
        for (let j = -radius; j <= radius; j++) {
          const px = Math.floor(ripple.x + i);
          const py = Math.floor(ripple.y + j);

          if (px >= 0 && px < resolution && py >= 0 && py < resolution) {
            const distance = Math.sqrt(i * i + j * j);
            if (distance <= radius) {
              const factor = (1 - distance / radius) * intensity;
              buffer[py][px] = factor;
            }
          }
        }
      }
    },
    [resolution],
  );

  // Update wave simulation
  const updateSimulation = useCallback(() => {
    const { current, previous } = bufferRef.current;
    const nextBuffer = Array(resolution)
      .fill(0)
      .map(() => Array(resolution).fill(0));

    // Wave equation simulation using finite differences
    for (let i = 1; i < resolution - 1; i++) {
      for (let j = 1; j < resolution - 1; j++) {
        const val =
          (current[i - 1][j] +
            current[i + 1][j] +
            current[i][j - 1] +
            current[i][j + 1]) /
            2 -
          previous[i][j];

        nextBuffer[i][j] = val * damping;
      }
    }

    // Update buffers
    bufferRef.current.previous = current;
    bufferRef.current.current = nextBuffer;
  }, [resolution, damping]);

  // Render the wave simulation to canvas
  const render = useCallback(() => {
    if (!canvasRef.current || !contextRef.current) return;

    const ctx = contextRef.current;
    const imageData = ctx.getImageData(0, 0, width, height);
    const { data } = imageData;
    const buffer = bufferRef.current.current;

    // Map simulation values to pixel colors
    for (let i = 0; i < resolution; i++) {
      for (let j = 0; j < resolution; j++) {
        const value = buffer[i][j];
        const x = Math.floor((j / resolution) * width);
        const y = Math.floor((i / resolution) * height);
        const pixelIndex = (y * width + x) * 4;

        // Create a water-like effect using value as displacement
        const intensity = Math.min(Math.abs(value) * 255, 255);
        data[pixelIndex] = intensity; // R
        data[pixelIndex + 1] = intensity; // G
        data[pixelIndex + 2] = intensity; // B
        data[pixelIndex + 3] = intensity * 0.5; // A
      }
    }

    ctx.putImageData(imageData, 0, 0);
  }, [width, height, resolution]);

  // Animation loop
  const animate = useCallback(() => {
    updateSimulation();
    render();
    frameRef.current = requestAnimationFrame(animate);
  }, [updateSimulation, render]);

  // Initialize canvas and start animation
  useEffect(() => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current;
    canvas.width = width;
    canvas.height = height;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    contextRef.current = ctx;
    initializeBuffers();
    animate();
    onReady?.();

    return () => {
      if (frameRef.current) {
        cancelAnimationFrame(frameRef.current);
      }
    };
  }, [width, height, initializeBuffers, animate, onReady]);

  // Handle mouse/touch interaction
  const handleInteraction = useCallback(
    (e: React.MouseEvent | React.TouchEvent) => {
      const canvas = canvasRef.current;
      if (!canvas) return;

      const rect = canvas.getBoundingClientRect();
      const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
      const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;

      const x = (clientX - rect.left) / rect.width;
      const y = (clientY - rect.top) / rect.height;

      addRipple(x, y, 1);
    },
    [addRipple],
  );

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        mixBlendMode: 'overlay',
        opacity: 0.8,
      }}
      onMouseMove={handleInteraction}
      onTouchMove={handleInteraction}
    />
  );
};

export default RippleEffect;

