const CarouselView: React.FC<CarouselViewProps> = ({ images, zoom }) => {
  if (!images.length) {
    return <div className="text-center text-gray-500 mt-8">No images to display</div>;
  }

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    adaptiveHeight: true,
    customPaging: (i: number) => (
      <div className="w-3 h-3 mx-1 rounded-full bg-white/50 hover:bg-white/70 transition-colors duration-300" />
    ),
  };

  return (
    <div className="carousel-view">
      <Slider {...settings}>
        {images.map((image, index) => (
          <motion.div
            key={image.id}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
            className="outline-none"
          >
            <div className="relative aspect-video">
              <img
                src={image.src}
                alt={image.alt}
                className="w-full h-full object-contain"
                style={{
                  maxHeight: `${600 * zoom}px`,
                }}
              />
              <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/70 to-transparent p-4">
                <h3 className="text-white text-lg font-semibold">{image.title}</h3>
                <p className="text-white/80 text-sm">
                  {image.width}  {image.height}
                </p>
              </div>
            </div>
          </motion.div>
        ))}
      </Slider>
    </div>
  );
};

export default CarouselView;

import { renderHook, act } from '@testing-library/react';
import useResizeObserver from '../useResizeObserver';

// Mock ResizeObserver
class ResizeObserverMock {
  callback: (entries: any[]) => void;

  constructor(callback: (entries: any[]) => void) {
    this.callback = callback;
  }

  observe() {
    // Mock implementation
  }

  disconnect() {
    // Mock implementation
  }

  // Helper to trigger resize
  mockResize(width: number, height: number) {
    this.callback([
      {
        contentRect: {
          width,
          height,
          x: 0,
          y: 0,
          top: 0,
          right: width,
          bottom: height,
          left: 0,
        },
        target: document.createElement('div'),
      },
    ]);
  }
}

// Setup global mock
global.ResizeObserver = ResizeObserverMock as any;

describe('useResizeObserver', () => {
  beforeEach(() => {
    jest.useFakeTimers();
  });

  afterEach(() => {
    jest.useRealTimers();
  });

  it('should initialize with default size', () => {
    const { result } = renderHook(() => useResizeObserver());
    expect(result.current.size).toEqual({ width: 0, height: 0 });
  });

  it('should update size after resize', () => {
    const { result } = renderHook(() => useResizeObserver());

    // Get the ResizeObserver instance
    const observer = (global.ResizeObserver as any).mock.instances[0];

    act(() => {
      observer.mockResize(100, 200);
      jest.runAllTimers(); // Run debounce timer
    });

    expect(result.current.size).toEqual({ width: 100, height: 200 });
  });

  it('should debounce resize updates', () => {
    const { result } = renderHook(() => useResizeObserver());
    const observer = (global.ResizeObserver as any).mock.instances[0];

    act(() => {
      observer.mockResize(100, 100);
      observer.mockResize(200, 200);
      observer.mockResize(300, 300);
      jest.advanceTimersByTime(100); // Not enough time for debounce
    });

    // Size should not have updated yet
    expect(result.current.size).toEqual({ width: 0, height: 0 });

    act(() => {
      jest.runAllTimers(); // Complete the debounce
    });

    // Should have the last size
    expect(result.current.size).toEqual({ width: 300, height: 300 });
  });

  it('should cleanup observer on unmount', () => {
    const disconnect = jest.fn();
    (global.ResizeObserver as any).mockImplementation(() => ({
      observe: jest.fn(),
      disconnect,
    }));

    const { unmount } = renderHook(() => useResizeObserver());
    unmount();

    expect(disconnect).toHaveBeenCalled();
  });

  it('should not update if size has not changed', () => {
    const { result } = renderHook(() => useResizeObserver());
    const observer = (global.ResizeObserver as any).mock.instances[0];

    act(() => {
      observer.mockResize(100, 100);
      jest.runAllTimers();
    });

    const initialRender = result.current;

    act(() => {
      observer.mockResize(100, 100); // Same size
      jest.runAllTimers();
    });

    // Should be the same object reference
    expect(result.current).toBe(initialRender);
  });
});

import axios from 'axios';
import { ImageInfo, FolderInfo } from '../types.js';

const api = axios.create({
  baseURL: '/api', // Assuming your API routes are under /api
});

/**
 * Fetches the list of folders from the server.
 *
 * @returns {Promise<FolderInfo[]>} A promise that resolves to an array of folder information.
 * @throws Will throw an error if the API request fails.
 */
export async function getFolders(): Promise<FolderInfo[]> {
  try {
    const response = await api.get('/folders');
    return response.data.map((folder: { name: string; path: string }) => ({
      name: folder.name,
      path: folder.path,
    }));
  } catch (error) {
    console.error('Error in getFolders:', error);
    throw new Error('Failed to fetch folders');
  }
}

/**
 * Fetches the list of images for a specific folder from the server.
 *
 * @param {string} folder - The name of the folder to fetch images from.
 * @returns {Promise<ImageInfo[]>} A promise that resolves to an array of image information.
 * @throws Will throw an error if the API request fails.
 */
export async function getImages(folder: string): Promise<ImageInfo[]> {
  if (!folder) {
    throw new Error('Folder parameter is required');
  }

  try {
    const response = await api.get(
      `/images?folder=${encodeURIComponent(folder)}`,
    );

    if (!Array.isArray(response.data)) {
      console.error('Invalid response data:', response.data);
      throw new Error('Invalid response format from server');
    }

    return response.data;
  } catch (error: any) {
    if (error.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      const message = error.response.data?.error || error.response.statusText;
      throw new Error(`Server error: ${message}`);
    } else if (error.request) {
      // The request was made but no response was received
      throw new Error('No response from server');
    } else {
      // Something happened in setting up the request that triggered an Error
      console.error('Error in getImages:', error);
      throw new Error(error.message || 'Failed to fetch images');
    }
  }
}
/**
 * Uploads files to a specified folder on the server.
 *
 * @param {string} folder - The name of the folder to upload files to.
 * @param {File[]} files - An array of File objects to be uploaded.
 * @param {function} onProgress - A callback function to report upload progress.
 * @returns {Promise<void>} A promise that resolves when the upload is complete.
 * @throws Will throw an error if the API request fails.
 */
export async function uploadFiles(
  folder: string,
  files: File[],
  onProgress: (progress: number) => void,
): Promise<void> {
  const formData = new FormData();
  formData.append('folder', folder);
  files.forEach((file) => formData.append('files', file));

  try {
    await api.post('/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
      onUploadProgress: (progressEvent) => {
        if (progressEvent.total) {
          const percentCompleted = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total,
          );
          onProgress(percentCompleted);
        }
      },
    });
  } catch (error) {
    console.error('Error in uploadFiles:', error);
    throw new Error('Failed to upload files');
  }
}
/**
 * Searches for images across all folders based on a query string.
 *
 * @param {string} query - The search query string.
 * @returns {Promise<ImageInfo[]>} A promise that resolves to an array of image information matching the search query.
 * @throws Will throw an error if the API request fails.
 */
export async function searchImages(query: string): Promise<ImageInfo[]> {
  try {
    const response = await api.get(`/search?q=${encodeURIComponent(query)}`);
    return response.data;
  } catch (error) {
    console.error('Error in searchImages:', error);
    throw new Error('Failed to search images');
  }
}

/**
 * Fetches the current main directory from the server.
 *
 * @returns {Promise<string>} A promise that resolves to the current main directory path.
 * @throws Will throw an error if the API request fails.
 */
export async function getCurrentDirectory(): Promise<string> {
  try {
    const response = await api.get('/getCurrentDirectory');
    return response.data.currentDirectory;
  } catch (error) {
    console.error('Error in getCurrentDirectory:', error);
    throw new Error('Failed to get current directory');
  }
}

import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout.js';
import MainContent from '../components/MainContent.js';
import { getFolders, getImages } from '../lib/api.js';
import { ImageInfo, FolderInfo, ViewMode } from '../types.js';

/**
 * Home component - the main page of the application.
 * It manages the overall state and layout of the app.
 *
 * @component
 * @returns {JSX.Element} The main application page.
 */
const Home: React.FC = () => {
  // State declarations for managing application data and UI
  const [folders, setFolders] = useState<FolderInfo[]>([]);
  const [selectedFolder, setSelectedFolder] = useState<string>('');
  const [images, setImages] = useState<ImageInfo[]>([]);
  const [zoom, setZoom] = useState<number>(1);
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [currentDirectory, setCurrentDirectory] = useState<string>('');
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [isGrouped, setIsGrouped] = useState<boolean>(true);
  const [viewMode, setViewMode] = useState<ViewMode>(ViewMode.GRID);

  /**
   * Effect hook to fetch the list of folders when the component mounts.
   * Sets the first folder as selected if available.
   */
  useEffect(() => {
    const fetchFolders = async () => {
      try {
        const folderList = await getFolders();
        setFolders(folderList);
        if (folderList.length > 0) {
          setSelectedFolder(folderList[0].name);
        }
      } catch (error) {
        console.error('Error fetching folders:', error);
        setError('Failed to fetch folders');
      }
    };

    fetchFolders();
  }, []);

  /**
   * Effect hook to fetch images when the selected folder changes.
   * Updates loading state and handles errors.
   */
  useEffect(() => {
    const fetchImages = async () => {
      if (!selectedFolder) return;

      setIsLoading(true);
      setError(null);

      try {
        const imageList = await getImages(selectedFolder);
        setImages(imageList);
      } catch (error) {
        console.error('Error fetching images:', error);
        setError('Failed to fetch images');
        setImages([]);
      } finally {
        setIsLoading(false);
      }
    };

    fetchImages();
  }, [selectedFolder]);

  /**
   * Handler for folder selection change.
   * Updates the selected folder state.
   * @param {string} folder - The newly selected folder.
   */
  const handleFolderChange = (folder: string) => {
    setSelectedFolder(folder);
  };

  /**
   * Handler for zoom level change.
   * Updates the zoom state.
   * @param {number} newZoom - The new zoom level.
   */
  const handleZoomChange = (newZoom: number) => {
    setZoom(newZoom);
  };

  /**
   * Handler for search query change.
   * Updates the search query state.
   * @param {string} query - The new search query.
   */
  const handleSearch = (query: string) => {
    setSearchQuery(query);
    // TODO: Implement search functionality
  };

  /**
   * Handler for file upload completion.
   * Refreshes the images in the current folder.
   */
  const handleUploadComplete = () => {
    // Refresh the images in the current folder
    getImages(selectedFolder).then(setImages);
  };

  /**
   * Handler for grouping toggle.
   * Toggles the grouping state.
   */
  const handleGroupToggle = () => {
    setIsGrouped(prevState => !prevState);
  };

  const handleViewModeChange = (newMode: ViewMode) => {
    setViewMode(newMode);
  };

  // Render the main layout with all necessary props
  return (
    <Layout
      folders={folders}
      selectedFolder={selectedFolder}
      onFolderChange={handleFolderChange}
      currentDirectory={selectedFolder}
      onSearch={handleSearch}
      zoom={zoom}
      onZoomChange={handleZoomChange}
      isGrouped={isGrouped}
      onGroupToggle={handleGroupToggle}
      viewMode={viewMode}
      onViewModeChange={handleViewModeChange}
    >
      <MainContent
        images={images}
        zoom={zoom}
        searchQuery={searchQuery}
        isLoading={isLoading}
        error={error}
        selectedFolder={selectedFolder}
        // onUploadComplete={handleUploadComplete}
        isGrouped={isGrouped}
        viewMode={viewMode}
      />
    </Layout>
  );
};

export default Home;

